public void fromXML (Element base)
    throws InvalidFontException, UnsupportedFontException {

  Map<Element, Block> blockCache = new HashMap<Element, Block> ();
  Block me = new Block (8, 2);
  me.setFixed (0, 1, 0);

  Element n = (Element) base.getFirstChild ();
  if (n != null
      && "horizontalAxis".equals (n.getTagName ())) {
    me.setOffset (4, axisTableFromXML (n, base, blockCache));
    n = (Element) n.getNextSibling (); }
  else {
    me.setOffset (4, null); }

  if (n != null
      && "verticalAxis".equals (n.getTagName ())) {
    me.setOffset (6, axisTableFromXML (n, base, blockCache)); }
  else {
    me.setOffset (6, null); }

  data = me.serialize ();
}
