public void fromXML (Element gpos)
    throws InvalidFontException, UnsupportedFontException {

  Map<Element, Block> blockCache = new HashMap<Element, Block> ();
  NodeList l = gpos.getChildNodes ();

  Block me = new Block (10, 3);
  me.setFixed (0, 1, 0);

  Map<String, Integer> lookupIndices = new HashMap<String, Integer> ();
  Block b1 = lookupListTableFromXML ((Element) l.item (2),
                                     gpos, blockCache,
                                     lookupIndices);

  Map  <String, Integer>featureIndices = new HashMap <String, Integer> ();
  Block b2 = featureListTableFromXML ((Element) l.item (1),
                                      gpos, blockCache,
                                      lookupIndices, featureIndices);

  Block b3 = scriptListTableFromXML ((Element) l.item (0),
                                     gpos, blockCache,
                                     featureIndices);

  me.setOffset (4, b3);
  me.setOffset (6, b2);
  me.setOffset (8, b1);
  data = me.serialize ();
}
