singleSubstTable
  singleSubstTable |=
    attribute format { "any" },
    element subst {
      attribute in { text },
      attribute out { text }
    }*

  standaloneSingleSubstTable =
    element singleSubstTable { attribute id { text }, singleSubstTable }

  singleSubstTableOffset = attribute name { text } | singleSubstTable
