public static int bitForCodePoint (int cp) { int min = 0; int max = unicodeRanges.length - 1;
// invariant: if cp is covered by one of the ranges, then the // index of that range is between min and max (both included)
while (min <= max) { int s = (min + max) / 2; if (cp < unicodeRanges [s].first) { max = s - 1; } else if (unicodeRanges [s].last < cp) { min = s + 1; } else { if ( unicodeRanges [s].first <= cp && cp <= unicodeRanges [s].last) { return unicodeRanges [s].bit; } else { return -1; }}}
return -1; }

Test ulUnicodeRange support
  public boolean unicodeRangeSupported (int i) {
    int offset = 42 + 4 * (i / 32);
    long range = getuint32 (offset);
    return (range & (1 << (i % 32))) != 0;
  }

  public boolean codePageSupported (int i) {
    if (data.length < 86) {
      return false; }
    int offset = 78 + 4 * (i / 32);
    long range = getuint32 (offset);
    return (range & (1 << (i % 32))) != 0;
  }

fsType
  public int getFsType () {
    return getuint16 (8);
  }

  public boolean allowsSubsettingOutlineEmbedding () {
    int fsType = getFsType ();

    if (fsType == 0) {
      return true; }

    if (   ((fsType & 0x0004) != 0 || (fsType & 0x0008) != 0)
        && (fsType & 0x0100) == 0
        && (fsType & 0x0200) == 0) {
      return true; }

    return false;
  }
