Checksum method
  private long checksum (byte[] data) {
    long checksum = 0;
    int i = 0;
    while (i < data.length) {
      int b1 = data [i++] & 0xff;

      int b2 = 0;
      if (i < data.length) {
        b2 = data [i++] & 0xff; }

      int b3 = 0;
      if (i < data.length) {
        b3 = data [i++] & 0xff; }

      int b4 = 0;
      if (i < data.length) {
        b4 = data [i++] & 0xff; }

      int word = b1 << 24 | b2 <<16 | b3 << 8 | b4;
      checksum = (checksum + word) & 0xffffffffL; }

    return checksum;
  }
