Relax Schema for glyf table
glyf =
  element glyf {
    ( simple_glyph | empty_glyph | composite_glyph )*
  }

empty_glyph =
  element empty_glyph {
    attribute gid { text } }

glyph_attributes =
  attribute gid { text },
  attribute xMin { text },
  attribute yMin { text },
  attribute xMax { text },
  attribute yMax { text }
